package java_lee_1;

public class BooleanExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*불타입(boolean)변수는 true, false의 값만 가질 수 있음.
		true = 1, false = 0 으로 인식*/
		//bool 타입의 stop 변수 선언.
		boolean stop = true;
		if(stop) {
			System.out.println("중지합니다.");
			
		}else {
			System.out.println("시작합니다.");
		}
		
	}

}
