package java_lee_1;

public class charExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//문자형 변수 char
		char c1 = 'A';
		char c2 = 65; //아스키코드 값
		char c3 = 97;
		
		System.out.println(c1);
		System.out.println(c2);
		System.out.println(c3);
	}

}
